import React, { useState } from 'react';
import activityList from './activityList';

function Activities() {
    const [activities, setActivities] = useState(activityList);

    const handleReviewClick = (id) => {
        setActivities(activities.map(activity => {
            if (activity.id === id) {
                return { ...activity, reviews: activity.reviews + 1 };
            }
            return activity;
        }));
    };

    return (
        <div className='activities-infor'>
            <h1>Online Experiences</h1>
            <p className='heading-para'>
                Join unique interactive activities led by one-of-a-kind hosts—all without leaving home.
            </p>
            <div className='activity-items'>
                {activities.map((activity) => (
                    <div key={activity.id} className='activity-item'>
                        <div className="status">
                            {activity.status === 'available' ? (
                                <span className="available">Available</span>
                            ) : (
                                <span className="sold">Sold Out</span>
                            )}
                        </div>
                        <img 
                            src={activity.src} 
                            alt={activity.alt} 
                            className='images' 
                            onClick={() => handleReviewClick(activity.id)} 
                        />

                        <p className='rating-text'>{activity.rating} (<span className="reviews">{activity.reviews}</span>)<h2>.USA</h2></p>
                        <p>{activity.description}</p>
                        <p>{activity.amount}<p>/ person</p>  </p>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Activities;
